import axios from 'axois';
import React from 'react';

class App extends React.Component{
  state = {
    isLoding: true,
    movies: [],
  };
  getMovies = async() => {
    const movies = await axios.get('https://yts-proxy.now.sh/list_movies.js');
  }

  componentDidMount(){
    this.getMovies();
  }
  render(){
    const { isLoding } = this.state;
    return <div>{isLoading ? 'Loding' : 'we are ready'}</div>
  }
}

export default App;
