import axios from 'axios';
import React from 'react';
import Movie from './Movie';

class App extends React.Component{
  state = {
    isLoading: true,
    movies: [],
  };
  getMovies = async() => {
    const{
      data: {
        data: {movies},
      },
     } = await axios.get('https://yts-proxy.now.sh/list_movies.json');
     this.setState({movies, isLoading: false});
  };

  componentDidMount(){
    this.getMovies();
  }
  render(){
    const { isLoading, movies } = this.state;
    return (
    <div>
      {isLoading 
      ? 'Loding...' 
      : movies.map((movie) =>{
         console.log(movie); 
          return (
          <Movie 
            key={movie.id}
            id={movie.id}
            year={movie.year}
            title={movie.title}
           summary={movie.summary}
            poster={movie.poster}
           />
          );
      })}
   </div>
  );  
 }
}

export default App;
